/*
 Get past month, today, queryFrom and queryTo
 From Cookie, and if not create cookie
 */
function getDates() {
    var options = {},
        cookieDateValidateFrom,
        cookieDateValidateTo,
        validCookieDateFrom,
        validCookieDateTo,
        cookieArray,
        cookie = document.cookie;

    if (cookie) {
        // match cookie string to make sure it's what we are looking for
        var validCookie = cookie.match(/fromthis/gi);

        // check if dates are valid
        if (!_.isNull(validCookie)) {
            cookieArray = cookie.split('; ');
            cookieArray = cookieArray.filter(function (value) {
                return (value.indexOf('fromThis=') > -1 || value.indexOf('toThis=') > -1);
            });
            cookieArray.sort();
            cookieDateValidateFrom = cookieArray[0].split('=')[1];
            cookieDateValidateTo = cookieArray[1].split('=')[1];

            validCookieDateFrom = moment(cookieDateValidateFrom, 'YYYY/MM/DD').isValid();
            validCookieDateTo = moment(cookieDateValidateTo, 'YYYY/MM/DD').isValid();
        }
    }

    if (validCookieDateFrom && validCookieDateTo) {
        var epochFrom = new Date(cookieDateValidateFrom.replace('-', '/').replace('-', '/'));
        var epochTo = new Date(cookieDateValidateTo.replace('-', '/').replace('-', '/'));
        epochFrom.setDate(epochFrom.getDate());
        epochTo.setDate(epochTo.getDate());

        options.queryFrom = cookieDateValidateFrom;
        options.queryTo = cookieDateValidateTo;
        options.pastMonth = new Date(epochFrom).format('m/d/Y');
        options.today = new Date(epochTo).format('m/d/Y');
    } else {
        options.queryFrom = moment().subtract(1, 'months').format('YYYY-MM-DD');
        options.queryTo = moment().format('YYYY-MM-DD');
        options.pastMonth = moment().subtract(1, 'months').format('MM/DD/YYYY');
        options.today = moment().format('MM/DD/YYYY');
        writedateCookie(options.queryFrom, options.queryTo);
    }

    return options;
}

/**
 * Update Cookie
 * @param queryFrom
 * @param queryTo
 */
function writedateCookie(queryFrom, queryTo) {
    document.cookie = 'fromThis=' + queryFrom;
    document.cookie = 'toThis=' + queryTo;
}

