// go and get notes then isolate priority data and display in the p1 field as a counted#
function displayP1DataAndNotesButton(mapPageIDwithTests, queryFrom, queryTo) {
    var notesXHRuri = 'https://itsc.autotrader.com:3000/scorecard/daily_notes?query={%22opened_at%22:{%22$gte%22:%22' + queryFrom + '%22,%22$lte%22:%22' + queryTo + '%22}}&sort=opened_at'; // get all the notes for all ids for a particular date range

    $.ajax({
        url: notesXHRuri,
        context: {localMapPageIDWithTests: mapPageIDwithTests},
        // xhrFields: {
        //    withCredentials: true
        // },
        success: function (data) {
            var mapPageIDWithCount = {};  //map to include the total count of the pageId(including the pageID itself and the tests associated with it in the config file)
            var mapPageIDData = this.localMapPageIDWithTests;
            var notes = data,
                items = _.pluck(notes, 'page_id'), // get all the pageids

                countedItems = _.countBy(items, function (n) {
                    return this.floor(n);
                }, Math); // count how many of each pageid there is
            for (var obj in countedItems) {
                for (var ctrPageId in mapPageIDData) {
                    if ((obj === ctrPageId) || ($.inArray(Number(obj), mapPageIDData[ctrPageId]) !== -1)) {
                        if (mapPageIDWithCount[ctrPageId] !== undefined) {
                            mapPageIDWithCount[ctrPageId] += countedItems[obj];
                        } else {
                            mapPageIDWithCount[ctrPageId] = countedItems[obj];
                        }
                    }
                }
            }

            for (var obja in mapPageIDWithCount) {
                var page_id = obja,
                    rowsetTR = $(".pd" + page_id);
                rowsetTR.text(mapPageIDWithCount[obja]); // apply the count# to the tr box
                rowsetTR.parent().find('.btn-notes').show(); // show notes btn if there is a note count
            }
            initTagButtons(queryFrom, queryTo);
        },
        error: function (req, status, err) {
            var errorType = req.status;
            console.log("error", errorType);
        }
    });
}


