function loadSparkDyn(IDChain, chainData, a_isNorthAmericanAuction) {
    var localID = IDChain,
        localData = chainData,
        Tval = 0,
        Pval = 0,
        successCount = 0,
        totalCount = 0,
        TtrendAv = 0,
        PtrendAv = 0;
    T_TREND_VAL = [];
    P_TREND_VAL = [];



    $.ajax({
        url: localData,
        // xhrFields: {
        //    withCredentials: true
        // },
        success: function (jdata) {
                //Parse data from the json values
                if (a_isNorthAmericanAuction === false) {
                    for (var i = 0, len = jdata.length; i < len; i++) {
                        T_TREND_VAL.push(jdata[i].availability);
                        P_TREND_VAL.push(jdata[i].performance);
                        Pval = Pval + jdata[i].performance;
                        successCount += jdata[i].count;
                        if (hasProp(jdata[i], "total")) {
                            totalCount += jdata[i].total;
                        } else {
                            totalCount += getTotal(jdata[i].count, jdata[i].availability);
                        }
                    }
                    //get the average of values vs dates
                    TtrendAv = (successCount / totalCount) * 100;
                } else {
                    for (var j = 0; j < jdata.length; j++) {
                        T_TREND_VAL.push(jdata[j].availability);
                        P_TREND_VAL.push(jdata[j].performance);
                        Pval = Pval + jdata[j].performance;
                        totalCount += jdata[j].availability;
                    }
                    //get the average of values vs dates
                    TtrendAv = totalCount / (jdata.length);
                }

                //get the average of values vs dates
                PtrendAv = Pval / jdata.length;

                //Round it off
                TtrendAv = TtrendAv.toFixed(2);
                PtrendAv = PtrendAv.toFixed(2);

                // Plug em into the table
                if (_.isEmpty(T_TREND_VAL) || _.isNull(T_TREND_VAL)) {
                    $('#' + localID + '-avail').text('No Data').css('font-size', '0.8em').css('color', '#F72D00');
                    $('#' + localID + '-perf').text('No Data').css('font-size', '0.7em').css('color', '#F72D00');
                    $('#' + localID + '-availtrend').text('No Data').css('font-size', '0.7em').css('color', '#F72D00');
                    $('#' + localID + '-perftrend').text('No Data').css('font-size', '0.7em').css('color', '#F72D00');
                } else {
                    var availWidth = $('#' + localID + '-availtrend').parent().width();
                    $('#' + localID + '-avail').text(TtrendAv);
                    $('#' + localID + '-perf').text(PtrendAv);
                    $('#' + localID + '-availtrend').sparkline(T_TREND_VAL, {
                        width: availWidth,
                        lineColor: '#238C00',
                        fillColor: '#B3FF99'
                    });
                    $('#' + localID + '-perftrend').sparkline(P_TREND_VAL, {
                        width: availWidth,
                        lineColor: '#238C00',
                        fillColor: '#B3FF99'
                    });
                    tagCells(localID, PtrendAv, TtrendAv);
                    T_TREND_VAL = [];
                    P_TREND_VAL = [];
                }
        },
        error: function (req, status, err) {
            var errorType = req.status;
            console.log("error", errorType);

            $('#' + localID + '-avail').text('No Data').css('font-size', '0.8em').css('color', '#F72D00');
            $('#' + localID + '-perf').text('No Data').css('font-size', '0.7em').css('color', '#F72D00');
            $('#' + localID + '-availtrend').text('No Data').css('font-size', '0.7em').css('color', '#F72D00');
            $('#' + localID + '-perftrend').text('No Data').css('font-size', '0.7em').css('color', '#F72D00');
        }
    });
}

