// Plots the big graphs with date from above
function plotTheGraph(chartObjectOptions) {

    // helper for returning the weekends in a period
    function weekendAreas(axes) {

        var markings = [],
            d = new Date(axes.xaxis.min);

        // go to the first Saturday
        d.setUTCDate(d.getUTCDate() - ((d.getUTCDay() + 1) % 7));
        d.setUTCSeconds(0);
        d.setUTCMinutes(0);
        d.setUTCHours(0);

        var i = d.getTime();

        // when we don't set yaxis, the rectangle automatically
        // extends to infinity upwards and downwards

        do {
            markings.push({xaxis: {from: i, to: i + 2 * 24 * 60 * 60 * 1000}});
            i += 7 * 24 * 60 * 60 * 1000;
        } while (i < axes.xaxis.max);

        return markings;
    }


    var plot = $.plot("#" + chartObjectOptions.localDiagID, [
            { // main plot points and line bar
                data: chartObjectOptions.d,
                lines: {
                    show: true,
                    lineWidth: 2,
                    fill: true,
                    fillColor: 'rgba(179, 255, 153, 0.3)'
                },
                points: {
                    show: true,
                    radius: 3,
                    symbol: "circle"
                },
                shadowSize: 0
            },
            { // priority alerts base line
                data: chartObjectOptions.dPriority,
                lines: {
                    show: false
                },
                points: {
                    show: true,
                    radius: 7,
                    fillColor: 'rgba(255, 204, 0, 0.9)',
                    symbol: function (ctx, x, y, radius, shadow) { // Custom triangle symbol
                        var size = radius * Math.sqrt(2 * Math.PI / Math.sin(Math.PI / 3));
                        var height = size * Math.sin(Math.PI / 3);
                        ctx.moveTo(x - size / 2, y + height / 2);
                        ctx.lineTo(x + size / 2, y + height / 2);
                        //ctx.lineTo(size , height);
                        ctx.strokeStyle = 'rgba(204, 0, 0, 0.5)';
                        if (!shadow) {
                            ctx.lineTo(x, y - height / 2);
                            ctx.lineTo(x - size / 2, y + height / 2);
                        }
                    }
                },
                shadowSize: 0

            },
            { // base line
                data: chartObjectOptions.d2,
                lines: {
                    show: true,
                    lineWidth: 2
                },
            }],
        { // legend and axis formatting
            colors: ['#175E00', '#00468C'],
            series: {
                lines: {
                    show: true,
                    lineWidth: 2
                }
            },
            xaxis: {
                mode: "time",
                timeformat: "%b %d",
                tickSize: (CHART_CASE === 'weekly') ? [1, "day"] : null, // show only per day if the case is weekly view to prevent hourly view between days
                timezone: 'browser'
            },
            yaxis: {
                min: chartObjectOptions.baseNum
            },
            selection: {
                mode: "x"
            },
            grid: {
                hoverable: true,
                clickable: true,
                markings: weekendAreas
            },
            font: {
                weight: 'bold'
            }
        }
    );

    var overview = $.plot("#" + chartObjectOptions.localBarID, [chartObjectOptions.d], {
        series: {
            lines: {
                show: true,
                lineWidth: 1,
                fill: true,
                fillColor: 'rgba(179, 255, 153, 0.3)'
            },
            shadowSize: 0
        },
        colors: ['#175E00'],

        xaxis: {
            //ticks: [],
            mode: "time",
            timeformat: "%b %d",
            timezone: 'browser'
        },
        yaxis: {
            //ticks: [],
            min: chartObjectOptions.baseNum
            //autoscaleMargin: 0.1
        },
        selection: {
            mode: "x"
        }
    });

    // Tooltips for plot point hovers
    $("#" + chartObjectOptions.localDiagID).bind("plothover", function (event, pos, item) {
        if (item) {

            var x = item.datapoint[0], y = item.datapoint[1].toFixed(2),
                thisDater = new Date(x),
                alertNote = item.series.data[item.dataIndex][3],
                alertTipHtml;

            thisDater.setDate(thisDater.getDate());
            thisDater = thisDater.format('M d, Y');

            // push over if too close to right edge!
            var leftValA = item.pageX + 18;
            if (($(window).width() - item.pageX) < 350) {
                leftValA = item.pageX - 350;
            }

            // A P1 alert plot point
            if (item.series.data[item.dataIndex][2] === "alert") {

                alertTipHtml = "<strong>Priority Alert</strong> | " + alertNote;
                $("#tooltip").html(alertTipHtml).css({
                    top: item.pageY + 18,
                    left: leftValA,
                    color: '#000',
                    backgroundColor: '#f1f1f1',
                    border: '1px solid #333',
                    padding: '0.8em',
                    borderRadius: '0.8em'

                }).fadeIn(200);


            } else { // Standard plot point tooltip
                // push over if too close to right edge!
                var leftValB = item.pageX + 5;
                if (($(window).width() - item.pageX) < 120) {
                    leftValB = item.pageX - 120;
                }

                $("#tooltip").html(thisDater + " = " + y).css({top: item.pageY + 5, left: leftValB}).fadeIn(200);
            }

        } else {
            $("#tooltip").hide();
        }
    });

    // now connect the two
    $("#" + chartObjectOptions.localDiagID).bind("plotselected", function (event, ranges) {

        // do the zooming
        $.each(plot.getXAxes(), function (_, axis) {
            var opts = axis.options;
            opts.min = ranges.xaxis.from;
            opts.max = ranges.xaxis.to;
        });
        plot.setupGrid();
        plot.draw();
        plot.clearSelection();

        // don't fire event on the overview to prevent eternal loop

        overview.setSelection(ranges, true);
    });

    $("#" + chartObjectOptions.localBarID).bind("plotselected", function (event, ranges) {
        plot.setSelection(ranges);
    });

    $('#kpi-1 .kpi-actual').text(chartObjectOptions.availAv + '%');
    $('#kpi-3 .kpi-actual').text(chartObjectOptions.perAv + 'ms');
    $('#kpi-1 .kpi-target').text(chartObjectOptions.avTar);
    $('#kpi-3 .kpi-target').text(chartObjectOptions.perTar);

    setTimeout(compairTrending, 500);

}
//Compair previous to current ot get the trending

function compairTrending() {
    var currentAvail = $('#availActual').attr('availNum'),
        currentPerf = $('#perfActual').attr('perfNum'),
        prevAvail = $('#availArrow').attr('availTrending'),
        prevPerf = $('#perfArrow').attr('perfTrending');

    currentAvail = parseFloat(currentAvail);
    currentPerf = parseFloat(currentPerf);
    prevAvail = parseFloat(prevAvail);
    prevPerf = parseFloat(prevPerf);

    $('#availArrow').removeClass('fa-arrow-up');
    $('#availArrow').removeClass('fa-arrow-down');
    $('#perfArrow').removeClass('fa-arrow-up');
    $('#perfArrow').removeClass('fa-arrow-down');

    if (currentAvail >= prevAvail) {
        $('#availArrow').addClass('fa-arrow-up').css('color', '#238C00');
    } else {
        $('#availArrow').addClass('fa-arrow-down').css('color', '#F72D00');
    }

    if (currentPerf <= prevPerf) {
        $('#perfArrow').addClass('fa-arrow-down').css('color', '#238C00');
    } else {
        $('#perfArrow').addClass('fa-arrow-up').css('color', '#F72D00');
    }
}

//Tag cells based on performance
function tagCells(chainedID, PtrendAv, TtrendAV) {
    var localID = chainedID,
        pAv = parseFloat(PtrendAv),
        tAv = parseFloat(TtrendAV),
        tTargetText = $('#' + localID + '-avail-target').text(),
        pTargetText = $('#' + localID + '-perf-target').text(),
        tTarget = parseFloat(tTargetText),
        pTarget = parseFloat(pTargetText),
        perRedline = pTarget * 2;

    tTarget = parseFloat(tTarget.toFixed(2));
    pTarget = parseFloat(pTarget.toFixed(2));
    perRedline = parseFloat(perRedline.toFixed(2));

    //Tag the Perf cell
    switch (true) {
        case (pAv <= pTarget):
            $('#' + localID + '-perf').addClass('green-color-icon');
            break;
        case (pAv > pTarget && pAv <= perRedline):
            $('#' + localID + '-perf').addClass('yellow-color-icon');
            break;
        case (pAv > perRedline):
            $('#' + localID + '-perf').addClass('red-color-icon');
            break;
        default:
            // Yarp
            break;
    }

    //Tag the Availabilty cell
    switch (true) {
        case (tAv >= tTarget):
            $('#' + localID + '-avail').addClass('green-color-icon');
            break;
        case (tAv >= 98.00 && tAv < tTarget):
            $('#' + localID + '-avail').addClass('yellow-color-icon');
            break;
        case (tAv < 98.00):
            $('#' + localID + '-avail').addClass('red-color-icon');
            break;
        default:
            // Smile and wave
            break;
    }
}

