						celldataCall = celldataCall.replace('fromThis', fromThis);
						celldataCall = celldataCall.replace('toThis', toThis);
						notesURI = thisDiv[j].notesURI;
						notesURI = notesURI.replace('fromThis', fromThis);
						notesURI = notesURI.replace('toThis', toThis);
