//Opens Edit Form and populates it with proper data
function editForm (_thisID, unit, page, pgID, avail, count, fromDate, _dataURI, updNotesURI){
	var thisUnit = unit;
	var thisPage = page;
	var pageID = pgID;
	var thisAvail = avail;
	var thisCount = count;
	var thisID = _thisID;
	var dataURI = _dataURI;
	var fromThis = moment(fromDate);
	var displayDate = moment(fromThis).format('MMMM DD, YYYY');
	var total, errorCount, converted;
	converted = thisAvail/100;
	total = thisCount/converted;
	total = Math.round(total);
	total = parseInt(total);
	errorCount = total - thisCount;

	if ( _.isNaN( count ) ) { // if no availability data set them to 0
	    total = 0;
	    errorCount = 0;
	    thisCount = 0;
	    thisAvail = 100;
	}
	// pull records for that day, sort correctly, grab last note
	updNotesURI = updNotesURI.replace('fromThis', fromDate).replace('toThis', fromDate).replace('sort=date_of_record', 'sort=opened_at');

	$.getJSON(updNotesURI, function(notes) {
		$("#edit-avail-tname").text(thisUnit);
		$("#edit-avail-form").attr("thisID", thisID);
		$("#edit-avail-form").attr("data-attrib-dataURI", dataURI);
		$("#thisTotal").text(total);
		$("#goodCount").text(thisCount);
		$("#thisAvail").text(thisAvail);
		$("#displayDate").text(displayDate);
		$("#inCount").val(errorCount);
		if (!_.isEmpty(notes)) {
			$("#inNote").val(notes[notes.length - 1].description);
		} else {
			$("#inNote").val('');
		}
		$("#edit-avail-form input, #edit-avail-form textarea").removeClass('error');
		$("#edit-avail-cont").fadeIn(); // show the edit avail overlay
		recUpdate(thisID, pageID, thisUnit, thisAvail, errorCount, total, fromThis);
		theCloser();
	});
}

