//Updates availablity record and allows user to enter notes as to "why"
function recUpdate(_thisID, pgID, unit, avail, errors, total, fromDate){
	var thisID = _thisID;
	var pageID = pgID;
	pageID = parseInt(pageID);
	var thisPage = unit;
	var thisUnit = unit;
	var thisCount;
	var thisAVail = avail;
	var theseErrors = errors;
	var thisTotal = total;
	var fromThis = moment(fromDate).format("YYYY-MM-DD");
	var today, opened_AT, newAvail, newNote, availSuccess, noteSuccess, logSuccess, actionType;

	// calculate the post values
	function calcValues(){
		theseErrors = $("#inCount").val();
		theseErrors = parseInt(theseErrors);
		thisCount = thisTotal - theseErrors;
		newAvail = thisCount / thisTotal;
		newAvail = newAvail * 100;
		newAvail = parseFloat(newAvail);
		newAvail = Number(newAvail.toFixed(2));

		today = new Date();
		opened_AT = new Date();
		today = today.format('c');
		opened_AT = opened_AT.format('m/d/Y');
	}
	$('#inCount').blur(function(){
	    calcValues();
		$('#goodCount').text(thisCount).addClass('currentPulse').delay(500).removeClass('currentPulse', 1000);
		$('#thisAvail').text(newAvail).addClass('currentPulse').delay(500).removeClass('currentPulse', 1000);
	}).focus(function(){
		$('#alertBox_avail').empty().removeClass('errorPulse');
	});


	$('#updateRecord').click(function(e){
	    e.preventDefault();
		var objId=$("#edit-avail-form").attr("thisID");
		if(objId!=thisID){
			return;
		}
	    calcValues();
		newNote = $('#inNote').val();
		var errorCountInput = $("#inCount").val();
		var dataURL = $("#edit-avail-form").attr("data-attrib-dataURI").split('?')[0];
		var notesURL = 'https://itsc.autotrader.com:3000/scorecard/daily_upd_notes/';

		// make sure the error count is not empty
		if ( isNaN( parseFloat( errorCountInput ) )  ) {
		    $("#inCount").addClass('error');
		} else {
		    $("#inCount").removeClass('error');
		}

		// make sure note is valid
		if ( _.isEmpty(newNote) ) {
		    $("#inNote").addClass('error');
		    $("#edit-avail-error").fadeIn();
		} else {
		    $("#inNote").removeClass('error');
		    $("#edit-avail-error").hide();
        }

		if ( !isNaN(parseFloat( errorCountInput ) ) && !_.isEmpty( newNote ) ) {
		  $.getJSON(dataURL + "/" + thisID, function(extractData){
			var prevName="", prevPage=thisPage, prevSource="", prevPerf=0, prevPageId=Number(pageID), prevUnit=thisUnit;
			if ( !_.isEmpty( extractData ) ) {
				//Get the corresponding previous data which is not modified
				prevName = ((typeof extractData.name) !== "undefined")?extractData.name: prevName;
				prevPage = ((typeof extractData.page) !== "undefined")?extractData.page: prevPage;
				prevSource = ((typeof extractData.source) !== "undefined")?extractData.source: prevSource;
				prevPerf = ((typeof extractData.performance) !== "undefined")?extractData.performance: prevPerf;
				//Some entries are missing the total field
				prevTotal = ((typeof extractData.total) !== "undefined")?extractData.total: (getTotal(extractData.count, extractData.availability));
				prevPageId = ((typeof extractData.page_id) !== "undefined")?extractData.page_id: prevPageId;
				prevUnit = ((typeof extractData.unit) !== "undefined")?extractData.unit: prevUnit;

				var availData = {
					"count": Number(thisCount),
					"name": prevName,
					"page": prevPage,
					"source": prevSource,
					"date": fromThis,
					"performance": prevPerf,
					"total": prevTotal,
					"page_id": prevPageId,
					"availability": Number(newAvail),
					"unit": prevUnit
				};

				var noteData = {
					"opened_at": today,
					"date": opened_AT,
					"page_id": prevPageId,
					"unit": thisUnit,
					"description": newNote,
					"date_of_record": fromThis
				};

				$.ajax({
					url: dataURL + "/" + thisID,
					type: "PUT",
					crossDomain: true,
					data: JSON.stringify(availData),
					contentType: "application/json; charset=utf-8",
					dataType: "json",
					success: function (data, textStatus, jqXHR) {
						$('#alertBox_avail').text('Record Updated!').addClass('successPulse');
						availSuccess = true;
						actionType = 'avail';
						actionLogger(today, actionType, availSuccess, thisID);
						if(!newNote || newNote === ''){
							console.log('Note field is empty');
							noteSucces = true;
							$('#updateRecord').unbind("click");
							setTimeout(closeBox, 2000);
						} else {
							$.ajax({
								url: notesURL,
								type: 'POST',
								crossDomain: true,
								data: JSON.stringify(noteData),
								contentType: "application/json; charset=utf-8",
								dataType: "json",
								success: function (data, textStatus, jqXHR) {
									$("#edit-avail-note-success").fadeIn().delay(3000).fadeOut();
									$("#edit-avail-cont").delay(2000).fadeOut();
									noteSuccess = true;
									actionType = 'note';
									actionLogger(today, actionType, noteSuccess, thisID);
									$('#updateRecord').unbind("click");
									setTimeout(closeBox, 2000);
								},
								error: function (req, status, err){
									var errorType = req.status;
									noteSuccess = false;
									actionType = 'note';
									actionLogger(today, actionType, noteSuccess, thisID);
								}
							});
						}
					},
					error: function (req, status, err){
						var errorType = req.status;
						$('#alertBox_avail').text('Unable to update record! Error Code: '+errorType).addClass('errorPulse');
						availSuccess = false;
						actionType = 'avail';
						actionLogger(today, actionType, availSuccess, thisID);
					}
				  });

			}
		 });
		}
	});
}

// Write actions to external log
function actionLogger(thisDate, thisType, thisState, thisPage){
	var today = thisDate;
	var logType = thisType;
	var currentState = thisState;
	var thisID = thisPage;
	var displayState, displayType;
	var logData = {};
	var logURL = 'https://itsc.autotrader.com:3000/scorecard/last_upd_log/';
	if (logType == 'avail'){
		displayType = 'Availability';
	}else if (logType == 'note'){
		displayType = 'Note';
	}

	if (currentState === true){
		displayState = 'Succeeded';
	}else{
		displayState = 'Failed';
	}

	logData = {
		"Timestamp": today,
		"Log_Type": displayType,
		"Log_State": displayState,
		"Record": thisID
	};

	$.ajax({
		  url: logURL,
		  type: 'POST',
		  crossDomain: true,
	      data: JSON.stringify(logData),
	      contentType: "application/json",
	      dataType: "json",
	      success: function (data, textStatus, jqXHR) {
              console.log('success!');
	      },
	      error: function (req, status, err){
	    	  var errorType = req.status;
	    	  console.log('error: '+errorType);
	      }
	});
}
