			var ctrLogDataElement = ($(currRow).find("input#"+_id+"-log-data"))[0];
			var ctrLogData = (ctrLogDataElement.getAttribute('data-attrib-last-log-data')!==ctrLogDataElement.value)?ctrLogDataElement.value:"";

